import styled from 'styled-components'

export const Container = styled.div`
  display: flex;
  width: 100%;
  align-items: center;
  flex-direction: column;
  .materialUIInput {
    display: block;
    box-sizing: border-box;
    width: 100%;
    border-radius: 4px;
    background-color: white;
    padding: 10px 15px;
    margin-bottom: 10px;
    font-size: 14px;
  }
`

export const Row = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: blue;
`

export const Column = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  margin: 4px;
`
