import React, { useEffect, useState } from 'react'

import { Container, CardDetail } from './styles'

const Cards: React.FC = props => {
  const [um, setUm] = useState('0')
  const [dez, setDez] = useState('0')
  const [vinte, setVinte] = useState('0')
  const [trinta, setTrinta] = useState('0')
  useEffect(() => {
    let total = 0
    let totalRendimento = 0
    props.lista.map((row, idx) => {
      totalRendimento += row.rendimento
      total += row.valorFinal
      if (idx < 13) {
        setUm({
          totalRendimento,
          total
        })
      } else if (idx < 121) {
        setDez({
          totalRendimento,
          total
        })
      } else if (idx < 241) {
        setVinte({
          totalRendimento,
          total
        })
      } else {
        setTrinta({
          totalRendimento,
          total
        })
      }
    })
  }, [props.lista])

  return (
    <Container>
      <CardDetail>{um.totalRendimento}</CardDetail>
      <CardDetail>{dez.totalRendimento}</CardDetail>
      <CardDetail>{vinte.totalRendimento}</CardDetail>
      <CardDetail>{trinta.totalRendimento}</CardDetail>
    </Container>
  )
}

export default Cards
