import styled from 'styled-components'

export const Container = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow-x: auto;
`

export const CardDetail = styled.div`
  width: 100%;
  padding: 12px;
  background: #fff;
  margin: 8px;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`
