import React from 'react'

import Lottie from 'react-lottie'
import loadginCash from '../../../../assets/loadingCash.json'
import Paper from '@material-ui/core/Paper'
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableContainer from '@material-ui/core/TableContainer'
import TableHead from '@material-ui/core/TableHead'
import TablePagination from '@material-ui/core/TablePagination'
import TableRow from '@material-ui/core/TableRow'
import { Container } from './styles'

interface Column {
  id: 'mes' | 'rendimento' | 'valorInicial' | 'aporteFinal'
  label: string
  minWidth?: number
  align?: 'right'
  format?: (value: number) => string
}

const columns: Column[] = [
  { id: 'mes', label: 'Mês', minWidth: 20 },
  { id: 'redimento', label: 'Rendimento (R$)', minWidth: 100 },
  {
    id: 'valorInicial',
    label: 'Valor Inicial (R$)',
    minWidth: 170,
    align: 'left',
    format: (value: number) => value.toLocaleString('pt-Br')
  },
  {
    id: 'aporteFinal',
    label: 'Aporte Final',
    minWidth: 170,
    align: 'left',
    format: (value: number) => value.toLocaleString('pt-Br')
  }
]

const List: React.FC = props => {
  const [page, setPage] = React.useState(0)
  const [rowsPerPage, setRowsPerPage] = React.useState(10)

  const handleChangePage = (event: unknown, newPage: number) => {
    setPage(newPage)
  }

  const handleChangeRowsPerPage = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setRowsPerPage(+event.target.value)
    setPage(0)
  }

  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: loadginCash,
    rendererSettings: {
      preserveAspectRatio: 'xMidYMid slice'
    }
  }
  return (
    <Container>
      {props.loading && (
        <Lottie options={defaultOptions} isStopped={false} isPaused={false} />
      )}
      {props.lista.length > 0 && (
        <Paper>
          <TableContainer>
            <Table stickyHeader aria-label="sticky table">
              <TableHead>
                <TableRow>
                  {columns.map(column => (
                    <TableCell
                      key={column.id}
                      align={column.align}
                      style={{ minWidth: column.minWidth }}
                    >
                      {column.label}
                    </TableCell>
                  ))}
                </TableRow>
              </TableHead>
              <TableBody>
                {props.lista
                  .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                  .map((row, idx) => {
                    return (
                      <TableRow hover role="checkbox" tabIndex={-1} key={idx}>
                        <TableCell key={idx}>{idx + 1}</TableCell>
                        <TableCell key={idx}>
                          {row.rendimento.toLocaleString('pt-br', {
                            style: 'currency',
                            currency: 'BRL'
                          })}
                        </TableCell>
                        <TableCell key={idx}>
                          {row.valorInicial.toLocaleString('pt-br', {
                            style: 'currency',
                            currency: 'BRL'
                          })}
                        </TableCell>
                        <TableCell key={idx}>
                          {row.aporteFinal.toLocaleString('pt-br', {
                            style: 'currency',
                            currency: 'BRL'
                          })}
                        </TableCell>
                      </TableRow>
                    )
                  })}
              </TableBody>
            </Table>
          </TableContainer>
          <TablePagination
            rowsPerPageOptions={[10, 25, 100]}
            component="div"
            count={360}
            rowsPerPage={rowsPerPage}
            page={page}
            onChangePage={handleChangePage}
            onChangeRowsPerPage={handleChangeRowsPerPage}
          />
        </Paper>
      )}
    </Container>
  )
}

export default List
