import React from 'react'
import { useForm, Controller } from 'react-hook-form'
import { Container, Row, Column } from './styles'

interface IFormInput {
  salarioMensal: number
  percentualAporte: number
  aporteMensal: number
  rendaNecessaria: number
}

const FormCalc: React.FC = () => {
  const { control, handleSubmit, register, setValue } = useForm<IFormInput>()

  const onSubmit = (data: IFormInput) => {
    console.log(data)
  }

  function onChangeRemunaration(e) {
    console.log(e.target.value)
    // setValue(maskMoney(e.target.value));
  }

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Container>
        <Row>
          <Column>
            <label>Salário Mensal</label>
            <Controller
              ref={register({
                maxLength: { value: 2, message: 'error message' }
              })}
              name="salarioMensal"
              control={control}
              defaultValue=""
              render={({ onChange, value }) => (
                <input
                  onChange={onChange}
                  onKeyPress={onChangeRemunaration}
                  value={value}
                />
              )}
            />
          </Column>
          <Column>
            <label>Percentual de Aporte</label>
            <Controller
              name="percentualAporte"
              control={control}
              defaultValue=""
              render={({ onChange, value }) => (
                <input onChange={onChange} value={value} />
              )}
            />
          </Column>
        </Row>
        <Row>
          <Column>
            <label>Aporte Mensal</label>
            <Controller
              name="aporteMensal"
              control={control}
              defaultValue=""
              render={({ onChange, value }) => (
                <input onChange={onChange} value={value} />
              )}
            />
          </Column>
          <Column>
            <label>Renda Necessária</label>
            <Controller
              name="rendaNecessaria"
              control={control}
              defaultValue=""
              render={({ onChange, value }) => (
                <input onChange={onChange} value={value} />
              )}
            />
          </Column>
        </Row>
        <input type="submit" />
      </Container>
    </form>
  )
}

export default FormCalc
