import styled from 'styled-components'

export const Container = styled.div`
  display: flex;
  width: 100%;
  align-items: center;
  background: red;


  /* .form {
    background: #0e101c;
    width: 100%;
    margin: 0 auto;
  } */

  button[type='button'] {
    background: black;
    border: 1px solid white;
  }

  p {
    color: #bf1650;
    text-align: center;
  }

  input {
    display: block;
    box-sizing: border-box;
    width: 100%;
    border-radius: 4px;
    border: 1px solid white;
    padding: 10px 15px;
    margin-bottom: 10px;
    font-size: 14px;
  }

  .materialUIInput {
    display: block;
    box-sizing: border-box;
    width: 100%;
    border-radius: 4px;
    background-color: white;
    padding: 10px 15px;
    margin-bottom: 10px;
    font-size: 14px;
  }
`

export const Row = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
`

export const Column = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
`
