import React, { useEffect, useState } from 'react'
import { useForm, Controller } from 'react-hook-form'
import Button from '@material-ui/core/Button'
import { Container, Row, Column } from './styles'
import { maskMoney } from '../MaksMoney'
import { maskDecimal } from '../MaskDecimal'
import { IFormInputJuros } from '../types'
import TextField from '@material-ui/core/TextField'
import InputAdornment from '@material-ui/core/InputAdornment'
import TrendingUpIcon from '@material-ui/icons/TrendingUp'
import DateRange from '@material-ui/icons/DateRange'
import Info from '../Info'
import { valueScaleCorrection } from 'framer-motion/types/render/dom/layout/scale-correction'

const FormCalcJurosCompostos: React.FC = () => {
  const [showModal, setShowModal] = useState(true)
  const {
    handleSubmit,
    register,
    setValue,
    errors,
    getValues,
    control
  } = useForm<IFormInputJuros>({
    defaultValues: {
      valorInicial: null,
      valorMensal: null,
      taxaJuros: null,
      tempoMeses: null
    }
  })

  const castFloat = valor => {
    return parseFloat(valor.toString().replace('.', '').replace(',', '.'))
  }

  const castInt = valor => {
    return parseInt(valor.toString())
  }

  const onSubmit = (data: IFormInputJuros) => {
    let valorInicial = castFloat(data.valorInicial)
    let valorMensal = castFloat(data.valorMensal)
    let taxaJuros = castFloat(data.taxaJuros)
    let tempoMeses = castInt(data.tempoMeses)

    let investimentoAcumulado = valorInicial
    var totalInvestimentoAcumulado = valorInicial + valorMensal * tempoMeses

    let jurosCompostos = 0
    let jurosCompostosTotal = 0

    for (let i = 0; i < tempoMeses; i++) {
      jurosCompostos = (investimentoAcumulado * taxaJuros) / 100
      jurosCompostosTotal += jurosCompostos
      investimentoAcumulado += jurosCompostos + valorMensal
    }

    var valorReceber = totalInvestimentoAcumulado + jurosCompostosTotal
    console.log('valor a receber', valorReceber)
    console.log('total investido', totalInvestimentoAcumulado)
    console.log('total rendimento', jurosCompostosTotal)
  }

  function onChangeLimpar() {
    setValue('valorInicial', undefined)
    setValue('valorMensal', undefined)
    setValue('taxaJuros', undefined)
    setValue('tempoMeses', undefined)
  }

  function onChangeValorInicial(e) {
    setValue('valorInicial', maskMoney(e.target.value))
  }

  function onChangeValorMensal(e) {
    setValue('valorMensal', maskMoney(e.target.value))
  }

  function onChangeTaxaJuros(e) {
    setValue('taxaJuros', maskDecimal(e?.target.value))
  }

  useEffect(() => {
    onChangeLimpar()
    setShowModal(localStorage.getItem(window.location.pathname) === null)
  }, [])

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Container>
        {showModal && (
          <Info>
            <h1>Aviso!</h1>
            <h3>Esta calculadora far谩 uma simula莽茫o de 30 anos.</h3>
            <p>
              Voc锚 colocar谩 seus ganhos mensais (Sal谩rio Mensal) e o percentual
              que voc锚 economizar谩 (Percentual do Aporte) para ter a renda que
              voc锚 j谩 recebe e o rendimento anual esperado (Rendimento Anual).
            </p>
          </Info>
        )}
        <Row>
          <Column>
            <Controller
              name="valorInicial"
              control={control}
              render={() => (
                <TextField
                  error={errors.valorInicial !== undefined}
                  id="standard-basic"
                  size="small"
                  fullWidth
                  label="Aporte Inicial"
                  placeholder="0,00"
                  onChange={onChangeValorInicial}
                  inputRef={register({
                    required: 'Campo obrigat贸rio'
                  })}
                  InputProps={{
                    name: 'valorInicial',
                    startAdornment: (
                      <InputAdornment position="start">R$</InputAdornment>
                    )
                  }}
                />
              )}
            />
          </Column>
          <Column>
            <Controller
              name="valorMensal"
              control={control}
              render={() => (
                <TextField
                  error={errors.valorMensal !== undefined}
                  id="standard-basic"
                  label="Aportes Mensal"
                  placeholder="0,00"
                  size="small"
                  fullWidth
                  onChange={onChangeValorMensal}
                  inputRef={register({
                    required: 'Campo obrigat贸rio'
                  })}
                  inputProps={{
                    name: 'valorMensal'
                  }}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">R$</InputAdornment>
                    )
                  }}
                />
              )}
            />
          </Column>
        </Row>
        <Row>
          <Column>
            <Controller
              name="taxaJuros"
              control={control}
              render={() => (
                <TextField
                  error={errors.taxaJuros !== undefined}
                  id="standard-basic"
                  label="Taxa Juros Mensal"
                  placeholder="100%"
                  onChange={onChangeTaxaJuros}
                  inputRef={register({
                    required: 'Campo obrigat贸rio',
                    maxLength: 6
                  })}
                  size="small"
                  fullWidth
                  inputProps={{
                    maxLength: 6,
                    name: 'taxaJuros'
                  }}
                  InputProps={{
                    name: 'taxaJuros',
                    startAdornment: (
                      <InputAdornment position="start">
                        <TrendingUpIcon />
                      </InputAdornment>
                    )
                  }}
                />
              )}
            />
          </Column>
          <Column>
            <Controller
              name="tempoMeses"
              control={control}
              render={() => (
                <TextField
                  error={errors.tempoMeses !== undefined}
                  id="standard-basic"
                  label="Tempo em meses"
                  placeholder="360"
                  inputRef={register({
                    required: 'Campo obrigat贸rio',
                    maxLength: 3
                  })}
                  size="small"
                  fullWidth
                  inputProps={{
                    maxLength: 3
                  }}
                  InputProps={{
                    name: 'tempoMeses',
                    startAdornment: (
                      <InputAdornment position="start">
                        <DateRange />
                      </InputAdornment>
                    )
                  }}
                />
              )}
            />
          </Column>
        </Row>
        <Row style={{ justifyContent: 'flex-end' }}>
          <Button
            variant="contained"
            color="primary"
            type="submit"
            style={{ marginRight: '8px' }}
            fullWidth
          >
            Calcular
          </Button>
          <Button
            type="button"
            variant="contained"
            onClick={onChangeLimpar}
            fullWidth
          >
            Limpar
          </Button>
        </Row>
      </Container>
    </form>
  )
}

export default FormCalcJurosCompostos
