import React, { useState } from 'react'
import { useForm, Controller } from 'react-hook-form'
import { Container, Row, Column } from './styles'
import { maskMoney } from '../../components/MaksMoney'

interface IFormInput {
  salarioMensal: number
  percentualAporte: number
  aporteMensal: number
  rendaRestante: number
  rendimentoAnual: number
}

const FormCalc: React.FC = () => {
  const [lista, setLista] = useState([])
  const {
    control,
    handleSubmit,
    register,
    setValue,
    errors,
    getValues
  } = useForm<IFormInput>()

  const onSubmit = (data: IFormInput) => {
    console.log(data)
    const redimentoMensal = 1 + parseFloat('rendimentoAnual') ** (1 / 12)
    for (let i = 0; i < 360; i++) {
      if (i === 0) {
        setLista([
          ...lista,
          {
            valorInicial: '000',
            aporteFinal: data.aporteMensal
          }
        ])
      } else {
        setLista([
          ...lista,
          {
            valorInicial: lista[i - 1].aporteFinal,
            rendimento: lista[i - 1].aporteFinal * (redimentoMensal / 100),
            aporteFinal:
              data.aporteMensal +
              lista[i - 1].aporteFinal +
              lista[i - 1].aporteFinal * (redimentoMensal / 100)
          }
        ])
      }
    }
    console.log(lista)
  }

  function calcAporteMensal() {
    const salarioMensal = parseFloat(
      getValues('salarioMensal').toString().replace('.', '').replace(',', '.')
    )
    const percentualAporte = parseInt(getValues('percentualAporte')) / 100
    const aporteMensal = parseFloat(percentualAporte * salarioMensal)

    console.log(salarioMensal)

    console.log(aporteMensal)

    if (aporteMensal) {
      setValue('aporteMensal', maskMoney((aporteMensal * 100).toString()))
      const rendaRestante = (salarioMensal - aporteMensal) * 100
      setValue('rendaRestante', maskMoney(rendaRestante.toString()))
    } else {
      setValue('aporteMensal', maskMoney('000'))
      setValue('rendaRestante', maskMoney('000'))
    }
  }

  function onChangeRemunaration(e) {
    setValue('salarioMensal', maskMoney(e.target.value))
    calcAporteMensal()
  }

  function onChangeLimpar() {
    setValue('salarioMensal', undefined)
    setValue('percentualAporte', undefined)
    setValue('aporteMensal', undefined)
    setValue('rendaRestante', undefined)
  }

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Container>
        <Row>
          <Column>
            <label>Sal√°rio Mensal</label>
            <input
              placeholder="R$ 0,00"
              className={errors.salarioMensal && 'inpt-error'}
              type="text"
              name="salarioMensal"
              onChange={onChangeRemunaration}
              ref={register({ required: true })}
            />
          </Column>
          <Column>
            <label>Percentual de Aporte</label>
            <input
              placeholder="100%"
              maxLength={3}
              className={errors.percentualAporte && 'inpt-error'}
              type="text"
              name="percentualAporte"
              onChange={calcAporteMensal}
              ref={register({ required: true, maxLength: 3 })}
            />
          </Column>
          <Column>
            <label>Rendomento Anual</label>
            <input
              placeholder="100%"
              maxLength={3}
              className={errors.rendimentoAnual && 'inpt-error'}
              type="text"
              name="rendimentoAnual"
              ref={register({ required: true, maxLength: 3 })}
            />
          </Column>
        </Row>
        <Row>
          <Column>
            <label>Aporte Mensal</label>
            <input
              type="text"
              name="aporteMensal"
              ref={register()}
              placeholder="R$ 0,00"
            />
          </Column>
          <Column>
            <label>Renda Restante</label>
            <input
              placeholder="R$ 0,00"
              type="text"
              name="rendaRestante"
              ref={register()}
            />
          </Column>
        </Row>
        <input type="submit" value="Calcular" />
        <input type="button" value="Limpar" onClick={onChangeLimpar} />
      </Container>
    </form>
  )
}

export default FormCalc
