import styled from 'styled-components'
import LoginPc from '../../assets/loginpc.png'

export const Container = styled.div`
  width: 100%;
  flex: 1 1 100%;
  padding: 45px 8px;
  display: flex;
  height: 85vh;
  position: relative;
  align-items: center;
  justify-content: flex-end;
  flex-direction: row;
  background: url(${LoginPc}) no-repeat;

  .card-login {
    position: absolute;
    background: #3f3e55;
    border-radius: 14px;
    width: 150px;
    height: 150px;
    left: 70%;
  }

  .imagem {
    display: none;
    svg {
      fill: #fff;
      width: 100%;
      height: 50%;
    }
  }
`
