import styled from 'styled-components'
import { motion } from 'framer-motion'

export const Container = styled.div`
  /* 




  .refresh {
    padding: 10px;
    position: absolute;
    background: rgba(0, 0, 0, 0.4);
    border-radius: 10px;
    width: 20px;
    height: 20px;
    top: 10px;
    right: 10px;
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;
  } */
`

export const IconPaceHolder = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  flex: 40px 0;
  margin-right: 20px;
`

export const TextPlaceHolder = styled.div`
  border-radius: 5px;
  width: 200px;
  height: 20px;
  flex: 1;
`

export const Li = styled(motion.li)`
  list-style: none;
  margin-bottom: 20px;
  display: flex;
  align-items: center;
  cursor: pointer;
`

export const Nav = styled(motion.nav)`
  position: inherit;
  top: 0;
  left: 0;
  bottom: 0;
  width: 300px;

  .background {
    position: inherit;
    top: 0;
    left: 0;
    bottom: 0;
    width: 300px;
    background: red;
  }
`

export const Ul = styled(motion.ul)`
  ul {
    padding: 25px;
    position: absolute;
    top: 100px;
    width: 230px;
  }
`

export const Button = styled.button`
  outline: none;
  border: none;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  cursor: pointer;
  position: absolute;
  top: 18px;
  left: 15px;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: transparent;
`
