import React, { useState } from 'react'
import { motion, AnimateSharedLayout } from 'framer-motion'
import { MenuItem } from './MenuItem'
import { Ul } from './styles'

const variants = {
  open: {
    transition: { staggerChildren: 0.07, delayChildren: 0.2 }
  },
  closed: {
    transition: { staggerChildren: 0.05, staggerDirection: -1 }
  }
}

export const Navigation = ({ toggle }) => {
  const [selecionado, setSelecionado] = useState(1)
  return (
    <AnimateSharedLayout>
      <Ul variants={variants}>
        {itemIds.map(menu => (
          <div onClick={() => onCLickMenu(menu, toggle, setSelecionado)}>
            <MenuItem
              menu={menu}
              key={menu.id}
              selecionado={selecionado === menu.id}
            />
          </div>
        ))}
      </Ul>
    </AnimateSharedLayout>
  )
}

function onCLickMenu(menu, toggle, setSelecionado) {
  setSelecionado(menu.id)
  toggle()
}

const itemIds = [
  {
    id: 1,
    rota: '/',
    titulo: 'Home'
  },
  {
    id: 2,
    rota: '/formcalc',
    titulo: 'Independência Financeira'
  }
]
