import React from 'react'
import Input from '@material-ui/core/Input'
import InputAdornment from '@material-ui/core/InputAdornment'
import { useForm, Controller } from 'react-hook-form'
import { Container, Row, Column } from './styles'
import { maskMoney } from '../../components/MaksMoney'

interface IFormInput {
  salarioMensal: number
  percentualAporte: number
  aporteMensal: number
  rendaNecessaria: number
}

const FormCalc: React.FC = () => {
  const { control, handleSubmit, register, setValue, errors } = useForm<
    IFormInput
  >()

  const onSubmit = (data: IFormInput) => {
    console.log(data)
  }

  function onChangeRemunaration(e) {
    setValue('salarioMensal', maskMoney(e.target.value))
  }

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Container>
        <Row>
          <Column>
            <label>Salário Mensal</label>
            <Controller
              ref={register({
                required: true
              })}
              name="salarioMensal"
              control={control}
              defaultValue=""
              render={({ onChange, value }) => (
                <Input
                  onChange={onChange}
                  onKeyPress={onChangeRemunaration}
                  value={value}
                  startAdornment={
                    <InputAdornment position="start">R$</InputAdornment>
                  }
                />
              )}
            />
            {errors.salarioMensal && 'Salário Mensal é obrigatório!'}
          </Column>
          <Column>
            <label>Percentual de Aporte</label>
            <Controller
              name="percentualAporte"
              control={control}
              defaultValue=""
              render={({ onChange, value }) => (
                <input onChange={onChange} value={value} />
              )}
            />
          </Column>
        </Row>
        <Row>
          <Column>
            <label>Aporte Mensal</label>
            <Controller
              name="aporteMensal"
              control={control}
              defaultValue=""
              render={({ onChange, value }) => (
                <input onChange={onChange} value={value} />
              )}
            />
          </Column>
          <Column>
            <label>Renda Necessária</label>
            <Controller
              name="rendaNecessaria"
              control={control}
              defaultValue=""
              render={({ onChange, value }) => (
                <input onChange={onChange} value={value} />
              )}
            />
          </Column>
        </Row>
        <input type="submit" />
      </Container>
    </form>
  )
}

export default FormCalc
