import React, { useEffect, useState } from 'react'

import { Container, CardDetail } from './styles'

const Cards: React.FC = props => {
  const [um, setUm] = useState('0')
  const [dez, setDez] = useState('0')
  const [vinte, setVinte] = useState('0')
  const [trinta, setTrinta] = useState('0')
  useEffect(() => {
    if (props.lista.length > 0) {
      setUm({
        totalRendimento: props.lista[11].rendimento.toLocaleString('pt-br', {
          style: 'currency',
          currency: 'BRL'
        }),
        total: props.lista[11].aporteFinal.toLocaleString('pt-br', {
          style: 'currency',
          currency: 'BRL'
        })
      })

      setDez({
        totalRendimento: props.lista[121].rendimento.toLocaleString('pt-br', {
          style: 'currency',
          currency: 'BRL'
        }),
        total: props.lista[121].aporteFinal.toLocaleString('pt-br', {
          style: 'currency',
          currency: 'BRL'
        })
      })

      setVinte({
        totalRendimento: props.lista[241].rendimento.toLocaleString('pt-br', {
          style: 'currency',
          currency: 'BRL'
        }),
        total: props.lista[241].aporteFinal.toLocaleString('pt-br', {
          style: 'currency',
          currency: 'BRL'
        })
      })

      setTrinta({
        totalRendimento: props.lista[241].rendimento.toLocaleString('pt-br', {
          style: 'currency',
          currency: 'BRL'
        }),
        total: props.lista[241].aporteFinal.toLocaleString('pt-br', {
          style: 'currency',
          currency: 'BRL'
        })
      })
    }
  }, [props.lista])

  return (
    <Container>
      <CardDetail>{um.totalRendimento}</CardDetail>
      <CardDetail>{dez.totalRendimento}</CardDetail>
      <CardDetail>{vinte.totalRendimento}</CardDetail>
      <CardDetail>{trinta.totalRendimento}</CardDetail>
    </Container>
  )
}

export default Cards
