import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Avatar from 'react-avatar'
import { Container, DivPage, Card, ContainerIntro } from '../styles/pages/Home'
import { Button } from '@material-ui/core'
import Link from 'next/link'

interface Parceiro {
  avatar: string
  url: string
  name: string
}

const Home: React.FC = () => {
  const [parceiro, setParceiro] = useState<Parceiro>()
  useEffect(() => {
    async function load() {
      const { data } = await axios.get('https://api.github.com/users/hugos94')
      setParceiro({
        avatar: data.avatar_url,
        url: data.html_url,
        name: data.name
      })
    }

    load()
  }, [])

  return (
    <Container>
      <ContainerIntro>
        <h4>Ferramentas do investidor.</h4>
        <h1>
          Aqui diponibilizamos algumas ferramentas que auxiliam simulações de
          investimento
        </h1>
        <Link href="/formcalc">Entre e faça sua simulação.</Link>
      </ContainerIntro>

      {/* <Button
        onClick={() => (window.location.href = '/formcalc')}
        variant="contained"
        color="primary"
        style={{
          marginTop: '40px'
        }}
      >
        Entrar
      </Button> */}

      {/* <DivPage>Parceiros</DivPage>
      <a href="https://tetofii.vercel.app" target="_blank">
        <Avatar
          round
          name={parceiro?.name}
          size="60"
          initials="2"
          src={parceiro?.avatar}
        />
      </a> */}
    </Container>
  )
}

export default Home
