import React, { useEffect, useState } from 'react'
import { useForm, Controller } from 'react-hook-form'
import Button from '@material-ui/core/Button'
import { Container, Row, Column } from './styles'
import { maskMoney } from '../MaksMoney'
import { maskDecimal } from '../MaskDecimal'
import { IFormInputJuros } from '../types'
import TextField from '@material-ui/core/TextField'
import InputAdornment from '@material-ui/core/InputAdornment'
import TrendingUpIcon from '@material-ui/icons/TrendingUp'
import DateRange from '@material-ui/icons/DateRange'
import Info from '../Info'

const FormCalcJurosCompostos: React.FC = () => {
  const [showModal, setShowModal] = useState(true)
  const {
    handleSubmit,
    register,
    setValue,
    errors,
    getValues,
    control
  } = useForm<IFormInputJuros>({
    defaultValues: {
      valorInicial: null,
      valorMensal: null,
      taxaJuros: null,
      tempoMeses: null
    }
  })

  const onSubmit = (data: IFormInputJuros) => {}

  function onChangeLimpar() {
    setValue('valorInicial', undefined)
    setValue('valorMensal', undefined)
    setValue('taxaJuros', undefined)
    setValue('tempoMeses', undefined)
  }

  function onChangeValorInicial(e) {
    setValue('valorInicial', maskMoney(e.target.value))
  }

  function onChangeValorMensal(e) {
    setValue('valorMensal', maskMoney(e.target.value))
  }

  useEffect(() => {
    onChangeLimpar()
    setShowModal(localStorage.getItem(window.location.pathname) === null)
  }, [])

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Container>
        {showModal && (
          <Info>
            <h1>Aviso!</h1>
            <h3>Esta calculadora fará uma simulação de 30 anos.</h3>
            <p>
              Você colocará seus ganhos mensais (Salário Mensal) e o percentual
              que você economizará (Percentual do Aporte) para ter a renda que
              você já recebe e o rendimento anual esperado (Rendimento Anual).
            </p>
          </Info>
        )}
        <Row>
          <Column>
            <Controller
              name="valorInicial"
              control={control}
              render={() => (
                <TextField
                  error={errors.valorInicial !== undefined}
                  id="standard-basic"
                  size="small"
                  fullWidth
                  label="Aporte Inicial"
                  placeholder="0,00"
                  onChange={e => onChangeValorInicial(e)}
                  inputRef={register({
                    required: 'Campo obrigatório'
                  })}
                  InputProps={{
                    name: 'valorInicial',
                    startAdornment: (
                      <InputAdornment position="start">R$</InputAdornment>
                    )
                  }}
                />
              )}
            />
          </Column>
          <Column>
            <Controller
              name="valorMensal"
              control={control}
              render={() => (
                <TextField
                  error={errors.valorMensal !== undefined}
                  id="standard-basic"
                  label="Aportes Mensal"
                  placeholder="0,00"
                  size="small"
                  fullWidth
                  onChange={e => onChangeValorMensal(e)}
                  inputRef={register({
                    required: 'Campo obrigatório',
                  })}
                  inputProps={{
                    name: 'valorMensal'
                  }}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">R$</InputAdornment>
                    )
                  }}
                />
              )}
            />
          </Column>
        </Row>
        <Row>
          <Column>
            <Controller
              name="taxaJuros"
              control={control}
              render={() => (
                <TextField
                  error={errors.taxaJuros !== undefined}
                  id="standard-basic"
                  label="Taxa Juros Mensal"
                  placeholder="100%"
                  inputRef={register({
                    required: 'Campo obrigatório',
                    maxLength: 6
                  })}
                  size="small"
                  fullWidth
                  inputProps={{
                    maxLength: 6
                  }}
                  InputProps={{
                    name: 'taxaJuros',
                    startAdornment: (
                      <InputAdornment position="start">
                        <TrendingUpIcon />
                      </InputAdornment>
                    )
                  }}
                />
              )}
            />
          </Column>
          <Column>
            <Controller
              name="tempoMeses"
              control={control}
              render={() => (
                <TextField
                  error={errors.tempoMeses !== undefined}
                  id="standard-basic"
                  label="Tempo em meses"
                  placeholder="360"
                  inputRef={register({
                    required: 'Campo obrigatório',
                    maxLength: 6
                  })}
                  size="small"
                  fullWidth
                  inputProps={{
                    maxLength: 6
                  }}
                  InputProps={{
                    name: 'tempoMeses',
                    startAdornment: (
                      <InputAdornment position="start">
                        <DateRange />
                      </InputAdornment>
                    )
                  }}
                />
              )}
            />
          </Column>
        </Row>
        <Row style={{ justifyContent: 'flex-end' }}>
          <Button
            variant="contained"
            color="primary"
            type="submit"
            style={{ marginRight: '8px' }}
            fullWidth
          >
            Calcular
          </Button>
          <Button
            type="button"
            variant="contained"
            onClick={onChangeLimpar}
            fullWidth
          >
            Limpar
          </Button>
        </Row>
      </Container>
    </form>
  )
}

export default FormCalcJurosCompostos
